css:
/* Hide a specific element in print view */
.text-center h4 {
    display: none;
}
.print-format td, .print-format th {
    padding: 4px !important;
}

html:
{% set result = frappe.call('general_ledger_customizations.api.prepared_doc_data.get_general_ledger_prepared_data') %}
{% if result %}
{% set filters = result.applied_filters %}
{% endif %}
{% set party = filters.party[0] if filters.party else filters.party_name %}
{% set party_type = filters.party_type %}
{% set company = filters.company %}
{% set header_name = ""%}
{% set customer_f1 = ""%}
{% set customer_f2 = ""%}
{%if party and party_type=="Supplier"%}
{%set header_name = "SERVICE PROVIDER LEDGER"%}
{% set is_nmicl = company == "Nationwide Medical Insurance Company limited" %}
{% if is_nmicl and party %}
    {% set party_parts = party.split(' ', 1) %}
    {% set party_code = party_parts[0] %}
    {% set party_name = party_parts[1] if party_parts|length > 1 else '' %}
{% else %}
    {% set party_code = "" %}
    {% set party_name = party %}
{% endif %}
{% set supplier_doc = frappe.get_doc("Supplier", party) %}
{% set address = supplier_doc.custom_postal_address or "" %}
{%endif%}
{%if party and party_type=="Customer"%}
{%set header_name = "CUSTOMER STATEMENT"%}
{% set customer_f1 = "Please pay any outstanding balance to avoid service suspension."%}
{% set customer_f2 = "NO PREMIUM NO COVER"%}
{% set customer_doc = frappe.get_doc("Customer", party) %}
{% set party_name = customer_doc.name%}
{% set address = customer_doc.custom_ghana_post_address or "" %}
{%endif%}
{% if result %}
{% set balances = result.balance_details %}
{% endif %}
{% set data = result.data %}
{% set from_date = frappe.utils.formatdate(filters.from_date, "dd MMM yyyy") %}
{% set to_date = frappe.utils.formatdate(filters.to_date, "dd MMM yyyy") %}


<table style="width:100%'">
    <tbody>
    <tr>
    <td colspan="4" style="width:30%;"><img src="/files/company_logo.png" alt="Company Logo" width="220" height="85"></td>
    {% if company =="MOBIMED PHARMACY LTD"%}
        <td colspan="8" style="font-weight:bold;line-height: 1.3;">
            <span style="font-size:14px;">Mobimed Pharmacy Limited.<br></span>
            <span style="font-size:12px; line-height: 1.3;">P. O. Box LG 1276<br></span>
            <span style="font-size:10px; line-height: 1.3;">Legon- Accra<br>
            Head Office Location: Osu Ako-Adjei, opposite Kingdom Books & Stationery Ltd.</span>
        </td>

    {% elif company=="HEALTHCARE NETWORKS LIMITED"%}
        <td colspan="8" style="font-weight:bold;line-height: 1.3;">
            <span style="font-size:14px;">Healthcare Network Limited<br></span>
            <span style="font-size:12px; line-height: 1.3;">P. O. Box LG 1276<br></span>
            <span style="font-size:10px; line-height: 1.3;">Legon- Accra<br>
            Head Office Location: Airport Residential, Nortei - Ababio Street</span>
        </td>

    {%else%}
    <td colspan="8" style="font-weight:bold;line-height: 1.3; text-align:left; white-space: nowrap;">
        <span style="font-size:12px; line-height: 1.3;">PMB 108 K.I.A ACCRA Digital Address: GA-053-5737 <br></span>
        <span style="font-size:10px; line-height: 1.3;">Tel: (0302) 226 892 Toll Free: (0800) 222 222<br>
        Email: finance@nationwidemh.com<br>
        Website: www.nationwidemh.com</span>
    </td>
    {%endif%}
    </tr>
    <tr>
        <td></td>
    </tr>
</tbody></table>

<table style="margin-top:-10px; table-layout:fixed; width:100%;">
<tr>
    <td colspan="14" rowspan="2" style="vertical-align:middle !important;text-align:center;font-weight:bold;">{{header_name or ""}}</td>
    <td style="font-size:12px; text-align:right;" colspan="4"><b>From</b> : {{from_date or ""}}</td>
</tr>
<tr>
    <td  style="font-size:12px; text-align:right;" colspan="4"><b>To</b> : {{to_date or ""}}</td>
</tr>
</table>


<table style="margin-top:-5px;table-layout:fixed; width:100%;border-top:1px solid black;font-size:11px;">
    <tr>
        <td rowspan="2" colspan="2" style="text-align:left;">
         <b>ID:</b>&nbsp;{{party_code or ""}}
        </td>
         <td colspan="10" style="text-align:left;">
         <b>Name:</b>&nbsp;{{party_name or ""}}
        </td>
    </tr>
    <tr>
       <td colspan="10" style="text-align:left;">
         <b>Address:</b>&nbsp;{{address or ""}}
        </td>
    </tr>
</table>
<table style="table-layout:fixed; width:100%;margin-top:10px;border-top:1px solid black;font-size:11px;">
    <tr>
        <td colspan="2" style="text-align:left;border-bottom:1px solid black;">
         <b>Date</b>
        </td>
         <td colspan="5" style="text-align:left;border-bottom:1px solid black;">
         <b>Narration</b>
        </td>
        <td colspan="2" style="text-align:right;border-bottom:1px solid black;">
         <b>Cheque#</b>
        </td>
        <td colspan="2" style="text-align:right;border-bottom:1px solid black;">
         <b>Debit</b>
        </td>
        <td colspan="2" style="text-align:right;border-bottom:1px solid black;">
         <b>Credit</b>
        </td>
        <td colspan="2" style="text-align:right;border-bottom:1px solid black;">
         <b>Balance<br>GHS</b>
        </td>
    </tr>

    <tr>
        <td colspan="2" style="text-align:left;"></td>
         <td colspan="5" style="text-align:left;">Balance Brought Forward</td>
        <td colspan="2" style="text-align:center;"></td>
        <td colspan="2" style="text-align:center;"></td>
        <td colspan="2" style="text-align:center;"></td>
        <td colspan="2" style="text-align:right;">
            {% set bal = balances.opening.balance or 0 %}
            {% if bal < 0 %}
                ({{ "{:,.2f}".format(bal | abs) }})
            {% else %}
                {{ "{:,.2f}".format(bal) }}
            {% endif %}
        </td>
    </tr>

    {% for i in data %}
    {% set bal = i.balance or 0 %}
    <tr style=" page-break-inside:avoid !important; background-color:{% if loop.index is odd %}#dedede{% endif %};">
        <td colspan="2" style="text-align:left;">{{ frappe.utils.formatdate(i.posting_date, "dd MMM yyyy") }}</td>
        <td colspan="5" style="text-align:left;">{{ i.get("remarks", "") }}</td>
        <td colspan="2" style="text-align:right;">
            {%if i.cheque_no%}
            {{i.cheque_no or ""}}
            {%endif%}
        </td>
        <td colspan="2" style="text-align:right;">{{ "{:,.2f}".format(i.debit or 0) }}</td>
        <td colspan="2" style="text-align:right;">{{ "{:,.2f}".format(i.credit or 0) }}</td>
        <td colspan="2" style="text-align:right;"> 
            {% if bal < 0 %}
                ({{ "{:,.2f}".format(bal | abs) }})
            {% else %}
                {{ "{:,.2f}".format(bal) }}
            {% endif %}
        </td>
    </tr>
    {% endfor %}

    <tr>
        <td colspan="2" style="text-align:left;"></td>
        <td colspan="5" style="text-align:left;"></td>
        <td colspan="2" style="text-align:center;"></td>
        <td colspan="2" style="text-align:right;"><b>{{ "{:,.2f}".format(balances.total.debit or 0) }}</b></td>
        <td colspan="2" style="text-align:right;"><b>{{ "{:,.2f}".format(balances.total.credit or 0) }}</b></td>
        <td colspan="2" style="text-align:right;"></td>
    </tr>
    <tr>
        <td colspan=14></td>
    </tr>
    <tr>
        <td colspan="2" style="text-align:left;"></td>
        <td colspan="5" style="text-align:left;"></td>
        <td colspan="2" style="text-align:center;"></td>
        <td colspan="2" style="text-align:center;"></td>
        <td colspan="1" style="text-align:center;"><b>Balance:</b></td>
        <td colspan="3" style="text-align:right;">
            <b style="background:#dedede; padding:4px 3px 0px 45px; border-right:2px solid black; border-bottom:2px solid black; display:inline-block;">
                {% set bal = balances.total.balance or 0 %}
                {% if bal < 0 %}
                    ({{ "{:,.2f}".format(bal | abs) }})
                {% else %}
                    {{ "{:,.2f}".format(bal) }}
                {% endif %}
            </b>
        </td>
    </tr>

    <tr>
        <td colspan="9" style="text-align:left;font-size:12px;font-weight:bold;font-style:italic;">{{customer_f1 or ""}}</td>
        <td colspan="5" style="text-align:left;color:red;font-size:14px;font-weight:bold;">{{customer_f2 or ""}}</td>

    </tr>

    <tr style="width:100% !important;">
        <td colspan="14" style="text-align:left; width:100% !important;">
            <table>
                <tr>
                    <td style="padding-top:13px !important; padding-right:0px !important;">
                        <b>Generated By:</b>
                    </td>
                    <td style="padding-left:0px !important">
                        {% set name = frappe.get_doc("User", frappe.session.user) %}
                        <div style="padding-bottom:20px !important; width:600px !important;">
                                <span style="border-bottom: 1px dotted #000; padding:5px 80px 5px 30px !important;">
                                    {{ name.full_name.upper() }}
                                </span>
                        </div>
                    </td>
                </tr>
            </table>
        </td>
    </tr>

</table>
